import { useState } from "react"
import { useDispatch } from "react-redux"
import { loginAttempt } from "../store/actions/postAction"


function Login() {

    const [inputs, setInputs] = useState({
        email: '',
        password: ''
    })
    const dispatch = useDispatch()

    const handleType = (e) => {
        e.persist()
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    const submitForm = (e) => {
        dispatch(loginAttempt(inputs.email, inputs,password))
    }

    return (
        <div id="body">
            <img id="bgbody" src="/mainbg.png"/>
            <form onSubmit={submitForm}>
                <input
                    name='email'
                    value={inputs.email}
                    onChange={handleType}
                    placeholder='Email Address'
                    required
                />
                <input
                    name='password'
                    value={inputs.password}
                    onChange={handleType}
                    placeholder='Password'
                    required
                />
                <button type='submit'>Login</button>
            </form>
            <style jsx>{`
                form {
                    width: 250px;
                    height: 450px;
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%,-50%);
                }
                #body {
                    float: left;
                    width: 100%;
                    height: 100%;
                    position: relative;
                }
                #bgbody {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    width: 100%;
                    height: 100%;
                    transform: translate(-50%,-50%);
                }
            `}</style>
        </div>
    )
}

export default Login
