import Theme from '../styles/theme'
import Head from "next/head";
import Link from 'next/link'
import { useDispatch, useSelector } from 'react-redux';
import { DropDown } from '../components/elements'
import { useState } from 'react'
import { signOut } from '../store/actions/postAction'

function Navigation({themeColor, setTheme}) {

    const {profile} = useSelector(state => state.post)
    const [active, setActive] = useState(false)

    const dispatch = useDispatch()

    const logout = () => {
        dispatch(signOut())
    }

    const toggleActive = (e) => {
        e.persist()
        if(active) setActive(false)
        else setActive(true)
    }

    console.log(themeColor)

    return (
        <div id="nav">
            <img src='/mainicon.png' className='logo'/>
            <Link href='/'><h1 className="title up">Synapse</h1></Link><Link href='/'><h1 className="title flash">Flash</h1></Link>
            <ul className='navLinks'>
                <Link href='/'><li>Home</li></Link>
                {profile ? null: <Link href='/login'><li>Login</li></Link>}
            </ul>
            <div className='profile' onClick={toggleActive}>
                <h1>Hi, {profile && profile.name}</h1>
                <img src="/userIcon.png"/>
            </div>
            <DropDown
                width='200px'
                height='auto'
                top='100px'
                right='10px'
                isActive={active}
                links={[
                    {action: ()=> {
                        if(themeColor === '#ffffff') setTheme(Theme.colors.dark);
                        else setTheme('#ffffff')
                    }, title: 'toggle theme'},
                    {href: '/admin', title: 'Admin'}, 
                    {href: '/dashboard', title: 'Dashboard'},
                    {href: '/myprofile', title: 'My Profile' }, 
                    {action: ()=> {
                        logout()
                        setActive(false)
                    }, title: 'Logout'}
                ]}
            />
            <style jsx>{`
                #nav {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100px;
                    transition: all .3s ease;
                    background: ${themeColor === '#ffffff' ? 'rgba(255,255,255,.9)': 'rgba(20,20,20,.9)'};
                    backdrop-filter: blur(12px);
                    z-index: 99;
                    box-shadow: ${Theme.sh.mat};
                }
                .profile {
                    position: absolute;
                    top: 0;
                    right: 10px;
                    width: 230px;
                    height: 100px;
                    cursor: pointer;
                    opacity: ${profile === null ? '0': '1'};
                }
                .profile img {
                    position: absolute;
                    top: 50%;
                    right: 20px;
                    width: 30px;
                    height: 30px;
                    transform: translate(0,-50%);
                }
                .profile h1 {
                    float: left;
                    font: 15px 'Roboto';
                    color: ${themeColor === '#ffffff' ? Theme.colors.dark: 'white'};
                    margin: 0;
                    margin-top: 50px;
                    transform: translate(-60%,-50%);
                    margin-left: 50%;
                }
                .navLinks {
                    float: left;
                    margin: 22px 20px;
                    padding: 10px 0;
                }
                .navLinks li {
                    float: left;
                    padding: 10px;
                    list-style: none;
                    color: ${themeColor === '#ffffff' ? Theme.colors.dark: 'white'};
                    font: 16px 'Roboto';
                    cursor: pointer;
                }
                .logo {
                    float: left;
                    width: 70px;
                    height: 70px;
                    padding: 15px;
                }
                .title {
                    float: left;
                    font: 38px ${Theme.font.title};
                    color: ${themeColor === '#ffffff' ? Theme.colors.dark: 'white'};
                    margin: 15px 2.5px;
                    padding: 10px 2.5px;
                    cursor: pointer;
                    transition: all .3s ease;
                }
                .up:hover {
                    transform: translateY(-2px);
                    text-shadow: 0 1px #000;
                }
                .flash {
                    background: -webkit-linear-gradient(left, ${Theme.colors.pink}, ${Theme.colors.purple}, ${Theme.colors.pinklight});
                    -webkit-background-clip: text;
                    -webkit-text-fill-color: transparent;
                }
            `}</style>
        </div>
    )
}

function Footer({themeColor}) {
    return (
        <div id="footer">

            <img src='/mainicon.png' className='logo'/>
            <Link href='/'><h1 className="title up">Synapse</h1></Link><Link href='/'><h1 className="title flash">Flash</h1></Link>
            <div className='bottomBar'>
                <h2>Copyright SynapseFlash © 2020</h2>
            </div>
            <style jsx>{`
                .bottomBar {
                    background: ${themeColor === '#ffffff' ? 'grey': 'black'};
                    min-height: 100px;
                    position: absolute;
                    bottom: 0;
                    left: 0;
                    width: 90%;
                    padding: 5px 5%;
                }
                .bottomBar h2 {
                    color: white;
                    float: left;
                    font: 18px 'Montserrat';
                    margin: 35px 30px;
                }
                .logo {
                    float: left;
                    width: 70px;
                    height: 70px;
                    padding: 15px;
                }
                .title {
                    float: left;
                    font: 38px ${Theme.font.title};
                    color: ${themeColor === '#ffffff' ? Theme.colors.dark: 'white'};
                    margin: 15px 2.5px;
                    padding: 10px 2.5px;
                    cursor: pointer;
                    transition: all .3s ease;
                }
                .up:hover {
                    transform: translateY(-2px);
                    text-shadow: 0 1px #fff;
                }
                .flash {
                    background: -webkit-linear-gradient(left, ${Theme.colors.pink}, ${Theme.colors.purple}, ${Theme.colors.pinklight});
                    -webkit-background-clip: text;
                    -webkit-text-fill-color: transparent;
                }
                #footer {
                    float: left;
                    position: relative;
                    width: 100%;
                    background: ${themeColor};
                    box-shadow: ${Theme.sh.mat};
                    min-height: 600px;
                }
            `}</style>
        </div>
    )
}

