import Link from 'next/link'
import { useSelector } from 'react-redux';
import Theme from '../styles/theme'
import { useState } from 'react'

function MyProfile({themeColor}) {
    const {profile, auth} = useSelector(state => state.post)
    const [inputs, setInputs] = useState({
        email: profile ? profile.email: null,
        name: profile.name,
        theme: profile.userTheme
    })

    const handleChange = (e) => {
        e.persist()
        setInputs({...inputs, [e.target.name]: e.target.value})
    }

    return (
        <div className='profile'>
            <img className='userIcon' src="/userIcon.png"/>
            <h1>{profile && profile.name}</h1>
            <form>
                <div className='section'>
                    <div className='break'></div>
                    <label>Email: </label>
                    <input 
                        name='email'
                        value={inputs.email}
                        onChange={handleChange}
                    />
                </div>
                <div className='break'></div>
                <label>Full Name: </label>
                <input 
                    name='name'
                    value={inputs.name}
                    onChange={handleChange}
                />
                <div className='break'></div>
                <label>Permission Level: </label>
                <h3>{profile && profile.permissionLevel}</h3>
                <div className='break'></div>
                <label>Default Theme Color: </label>
                <input 
                    name='theme'
                    value={inputs.theme}
                    onChange={handleChange}
                />
            </form>
                <Link href='/forgotpassword'><h2>Forgot Password</h2></Link>
            <style jsx>{`
                .profile {
                    float: left;
                    width: 80%;
                    margin: 40px 5%;
                    padding: 10px 5%;
                    min-height: 200px;
                    background: ${themeColor};
                    margin-top: 140px;
                    box-shadow: ${Theme.sh.mat};
                    border-radius: 4px;
                }
                .userIcon {
                    float: left;
                    width: 50px;
                    height: 50px;
                    margin: 10px 0;
                    margin-left: 50%;
                    transform: translateX(-50%);
                }
                h1 {
                    float: left;
                    margin: 10px;
                    font: 32px 'Montserrat';
                    width: 100%;
                    text-align: center;
                }
                .break {
                    float: left;
                    width: 100%;
                    margin: 5px 0;
                }
                form {
                    padding: 10px 5%;
                    float: left;
                    width: 90%;
                }
                form h3 {
                    list-style: none;
                    float: left;
                    margin: 10px 5px;
                    margin-bottom: 20px;
                    font: 16px 'Roboto';
                }
                form input {
                    float: left;
                }
                form label {
                    list-style: none;
                    float: left;
                    margin: 12px 5px;
                    font: 14px 'Roboto';
                }
                h2 {
                    float: left;
                    font: 16px 'Montserrat';
                    border-radius: 4px;
                    cursor: pointer;
                    box-shadow: ${Theme.sh.mat};
                    background: ${Theme.colors.pink};
                    color: white;
                    padding: 10px;
                    margin: 10px 5px;
                }
            `}</style>
        </div>
    )
}

export default MyProfile
