import { useState } from "react"
import { useSelector } from "react-redux"
import Theme from '../styles/theme'


function Dashboard() {

    const {profile} = useSelector(state => state.post)
    const [inputs, setInputs] = useState({
        thought: '', 
        user: profile.email
    })

    const handleType = (e) => {
        e.persist()
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div className='dash'>
            <div className='welcome'>
                <h1>Hello, {profile && profile.name}</h1>

            </div>
            <div className='uploadthought'>
                <form>
                    <label>Thought</label>
                    <textarea
                        name='thought'
                        value={inputs.thought}
                        onChange={handleType}
                        required
                    />
                </form>
            </div>
            <style jsx>{`
                .welcome {
                    float: left;
                    width: 90%;
                    margin: 30px 5%;
                    border-radius: 4px;
                    box-shadow: ${Theme.sh.mat};
                }
                .welcome h1 {
                    float: left;
                    font: 36px 'Montserrat';
                    padding: 20px;
                }
                .dash {
                    float: left;
                    width: 100%;
                    margin-top: 100px;
                    min-height: 800px;
                }
            `}</style>
        </div>
    )
}

export default Dashboard
