import { Children } from "react";
import { useScroll } from '../components/hooks'

export function ParallaxBanner({children, image, height}) {
    const currentScroll = useScroll();
    return (
        <div className='banner'>
            <img className='main_bg' src={image}/>
            <style jsx>{`
                div {
                    transform: ;
                    margin-top: ${-currentScroll.scrollY / 3}px;
                }
                .banner {
                    float: left;
                    width: 100%;
                    height: ${height};
                    overflow: hidden;
                    position: relative;
                }
                .main_bg {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    width: 100%;
                    transform: translate(-50%,-50%) translate3d(0,0,0);
                }
            `}</style>
        </div>
    )
}
