import Theme from '../styles/theme'
import Head from "next/head";
import Link from 'next/link'

function Navigation() {

    return (
        <div id="nav">
            <img src='/mainicon.png' className='logo'/>
            <Link href='/'><h1 id="title">Synapse Flash</h1></Link>
            <style jsx>{`
                #nav {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100px;
                    background: white;
                    z-index: 99;
                    box-shadow: ${Theme.sh.mat};
                }
                .logo {
                    float: left;
                    width: 80px;
                    height: 80px;
                    padding: 10px;
                }
                #title {
                    float: left;
                    font: 36px ${Theme.font.title};
                    cursor: pointer;
                    margin: 15px 10px;
                    padding: 10px 5px;
                    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
                    background: -webkit-linear-gradient(left, ${Theme.colors.pink}, ${Theme.colors.purple}, ${Theme.colors.pinklight});
                    -webkit-background-clip: text;
                    -webkit-text-fill-color: transparent;
                }
            `}</style>
        </div>
    )
}

function Layout({children}) {
    return (
        <div id='layout'>
            <Head>
                <link rel="preconnect" href="https://fonts.gstatic.com"/>
                <link href="https://fonts.googleapis.com/css2?family=Andika+New+Basic&family=Montserrat:wght@700&family=Roboto:wght@300&display=swap" rel="stylesheet"/>
            </Head>
            <Navigation></Navigation>
            {children}
            <style jsx global>{`
                #__next {
                    padding: 0;
                    margin: 0;
                }
                body {
                    padding: 0;
                    margin: 0;
                    width: 100%;
                    height: 100%;
                    margin-top: 100px;
                }
            `}</style>
        </div>
    )
}

export default Layout
