import { useState, useEffect } from "react"
import { useSelector, useDispatch } from "react-redux"
import Theme from '../styles/theme'
import { uploadThought } from '../services/thoughtservice'
import { fetchThoughts } from "../store/actions/postAction"
import Link from 'next/link'

function Dashboard({themeColor}) {

    const {profile, auth, thoughts} = useSelector(state => state.post)
    const dispatch = useDispatch()
    const [inputs, setInputs] = useState({
        thought: '', 
        user: profile,
        auth: auth
    })

    useEffect(() => {
        dispatch(fetchThoughts(profile.email, auth))
    }, [])

    const handleType = (e) => {
        e.persist()
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    const uploadNewThought = async (e) => {
        e.preventDefault()
        setInputs({...inputs, thought: ''})
        const newThought = await uploadThought(inputs.thought, profile.email, "normal", auth)
        if(newThought) console.log(newThought)
    }

    return (
        <div className='dash'>
            <Link href='/'><div className='link'>Back to home</div></Link>
            <div className='uploadThought'>
                <form onSubmit={uploadNewThought}>
                    <label>Thought</label>
                    <textarea
                        name='thought'
                        value={inputs.thought}
                        onChange={handleType}
                        required
                    />
                    <button type='submit'>Upload Thought</button>
                </form>
            </div>
            <style jsx>{`
                .item h2 {
                    position: absolute;
                    font: 14px 'Roboto';
                    bottom: 10px;
                    text-align: center;
                    left: 50%;
                    color: ${themeColor === '#ffffff' ? Theme.colors.dark: 'white'};
                    transform: translate(-50%);
                }
                .uploadThought {
                    float: left;
                    width: 70%;
                    margin: 30px 5%;
                    border-radius: 4px;
                    box-shadow: ${Theme.sh.mat};
                }
                .uploadThought form {
                    float: left;
                    width: 90%;
                    padding: 30px 5%;
                }
                .uploadThought label {
                    float: left;
                    margin: 10px 0;
                    font: 16px 'Roboto';
                }
                .uploadThought button {
                    float: left;
                    margin: 20px 0;
                }
                .uploadThought textarea {
                    float: left;
                    width: 95%;
                    padding: 25px 2.5%;
                    margin: 10px 0;
                    min-height: 150px;
                    resize: vertical;
                    font: 16px 'Roboto';
                }
                .uploadThought h1 {
                    float: left;
                    color: ${themeColor === '#ffffff' ? Theme.colors.dark: 'white'};
                    font: 32px 'Montserrat';
                    padding: 10px 40px;
                }
                .dash {
                    float: left;
                    width: 100%;
                    position: relative;
                    height: 100%;
                    overflow-y: hidden;
                }
            `}</style>
        </div>
    )
}

export default Dashboard
