import { useState } from "react"
import { useSelector } from "react-redux"
import Theme from '../styles/theme'
import { uploadThought } from '../services/thoughtservice'


function Dashboard() {

    const {profile} = useSelector(state => state.post)
    const [inputs, setInputs] = useState({
        thought: '', 
        user: profile.email
    })

    const handleType = (e) => {
        e.persist()
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    const uploadNewThought = (e) => {
        e.preventDefault()
        const newThought = uploadThought(inputs.thought, inputs.user)
    }

    return (
        <div className='dash'>
            <div className='welcome'>
                <h1>Hello, {profile && profile.name}</h1>
                <div className='list'>
                    <div className='item'>
                        <h2>Upload Thought</h2>
                    </div>
                    <div className='item'>
                        <h2>Thought History</h2>
                    </div>
                </div>
            </div>
            <div className='uploadthought'>
                <h1>Upload New Thought</h1>
                <form>
                    <label>Thought</label>
                    <textarea
                        name='thought'
                        value={inputs.thought}
                        onChange={handleType}
                        required
                    />
                </form>
            </div>
            <style jsx>{`
                .list {
                    float: left;
                    width: 100%;
                    display: flex;
                }
                .uploadthought {
                    float: left;
                    width: 90%;
                    margin: 30px 5%;
                    border-radius: 4px;
                    box-shadow: ${Theme.sh.mat};
                }
                .uploadthought form {
                    float: left;
                    width: 90%;
                    padding: 30px 5%;
                }
                .uploadthought label {
                    float: left;
                    margin: 10px 0;
                    border-bottom: 1px solid blue;
                    font: 16px 'Roboto';
                }
                .uploadthought textarea {
                    float: left;
                    width: 95%;
                    padding: 25px 2.5%;
                    margin: 10px 0;
                    min-height: 150px;
                    resize: vertical;
                    font: 16px 'Roboto';
                }
                .uploadthought h1 {
                    float: left;
                    font: 32px 'Montserrat';
                    padding: 10px 40px;
                }
                .welcome {
                    float: left;
                    width: 90%;
                    margin: 30px 5%;
                    border-radius: 4px;
                    box-shadow: ${Theme.sh.mat};
                }
                .welcome h1 {
                    float: left;
                    font: 32px 'Montserrat';
                    padding: 20px;
                }
                .dash {
                    float: left;
                    width: 100%;
                    margin-top: 100px;
                    min-height: 800px;
                }
            `}</style>
        </div>
    )
}

export default Dashboard
